installation.script:
#!/bin/bash
apt install bzip2 unzip
cd /mnt/server

# Download Zandronum 3.1 and set permissions
curl https://zandronum.com/download | grep -Eo "/downloads/zandronum[a-zA-Z0-9./?=_-]*" | grep -Eo "[a-zA-Z0-9./?=_-]*linux-x[a-zA-Z0-9./?=_-]*" | wget -nc "https://zandronum.com$(cat -)"  && tar -xvf *.tar.bz2 && sleep 1 ; rm *.tar.bz2

# Checks if lists exists and creates them if missing
if [ ! -f "adminlist.txt" ]; then
  touch adminlist.txt
fi

if [ ! -f "banlist.txt" ]; then
  touch banlist.txt
fi

if [ ! -f "whitelist.txt" ]; then
  touch whitelist.txt
fi

# Create server config if doesnt exist
if [ ! -f "server.cfg" ]; then
echo '// CONFIGURATION FILE FOR SERVERS

// Set the hostname.
sv_hostname "Pterodactyl Zandronum"


// Set the wad url - if you set this to the exact location of the zip file
// of the wad youre using then getwad will find it.
//sv_website "https://pterodactyl.io/"


// Set the banfile
sv_banfile "banlist.txt"

// Set the logfile.
//logfile "/zandronium/log.log"

// Force Skulltag to use the dmflags we want; dont set any defaults.
sv_defaultdmflags 0

// Reset the compat flags.
compatflags 0

// Make sure that we dont display launcher queries in the window.
// (Otherwise, logfiles will get HUGE!)
sv_showlauncherqueries false

// Set the skill level. 0 = Im too young to die, 4 = Nightmare
skill 2

//*** Set the game mode. ***
//To set the various gamemodes:
//Deathmatch (current setting)
deathmatch 1
//Duel
//duel 1
//Co-op
//deathmatch 0
//Teamgame
//teamgame 1
//Team Deathmatch
//teamplay 1
//Capture the Flag
//ctf 1
//Last Man Standing
//lastmanstanding 1
//Team Last Man Standing
//teamlms 1
//Terminator
//terminator 1
//One Flag CTF
//oneflagctf 1
//Skulltag
//skulltag 1
//Invasion
//invasion 1
//Survival
//survival 1
//Possession
//possession 1
//Team Possession
//teampossession 1

//Buckshot Mode
//buckshot 1

//Instagib Mode
//instagib 1

//Call Vote (Change to true if you want to disable it)
sv_nocallvote False

//Friendly Fire (0.00 means friendly fire disabled, 1.00
//means full team damage. 0.50 is half damage etc)
teamdamage 0.00

// Set the limits.
//fraglimit 25

// WINLIMIT is used for the max number of wins in a duel game
// before the map changes. 

// POINTLIMIT is used for the max number of points a team scores
// before the map changes (in modes like CTF or Domination)

// FRAGLIMIT is used for the max number of frags in either Team
// Deathmatch, regular Deathmatch, or the max frags for 1 Duel.

//TIMELIMIT is used to end the match based on the time.

// Set the maximum number of players who can participate in the game.
sv_maxplayers 8

// Set the maximum number of clients who can connect to the server.
sv_maxclients 10

//Fast Monsters
sv_fastmonsters True

//Respawn Invulnerability (Set to True to Disable)
sv_norespawninvul False

//Free Look (Set to True to Disable)
sv_nofreelook False

//Jump (Set to True to Disable)
sv_nojump False

//Makes this server a public one (Shows up on the master server.)
//Set this to False to make it private
sv_updatemaster True

//Change this setting to True if you plan on connecting to the server
//Over a local area network.
sv_broadcast True

// Set the message of the day.
sv_motd "Welcome to Zandronum in a Docker Container!"

// Set the RCON password.
sv_rconpassword "adminDocker"

// Disallow connect/join passwords.
sv_password ""


// Set some dmflags. Should be self-explanatory.
sv_weaponstay false
sv_nomonsters false
sv_itemrespawn false
sv_noexit false
sv_doubleammo true

// Spawn drops on the floor.
compat_notossdrops false' >> server.cfg
fi

# Create folder structure for Zandronum .ini
mkdir -p /mnt/server/~/.config/zandronum

# Create wad folder and import freeware
cd /mnt/server/~/.config/zandronum
wget https://github.com/freedoom/freedoom/releases/download/v0.12.1/freedoom-0.12.1.zip -O freedoom.zip
wget https://github.com/freedoom/freedoom/releases/download/v0.12.1/freedm-0.12.1.zip -O freedm.zip 
unzip -j -o freedoom.zip "*.wad"
unzip -j -o freedm.zip "*.wad"
rm *.zip
